import './Login.css';
import { useState } from 'react';

const loginMethod = ["Username", "Email"];
function loginInput({method}) {
    return (
        <>
            {method === 0 && 
            <div className="login__field">
                <label htmlFor="login-username">Username<span>*</span></label>
                <input type="text" id="login-username" placeholder="Input username"/>
            </div>}
            {method === 1 && 
                <div className="login__field">
                <label htmlFor="login-useremail">User Email<span>*</span></label>
                <input type="text" id="login-useremail" placeholder="Input username"/>
        </div>}
            }
        </>
    )
}

function Login(){
    const [method, setMethod] = useState(0);
    return (
        <form className='login__form'>
            <div className="login__field">
                <label htmlFor="login-username">Username<span>*</span></label>
                <input type="text" id="login-username" placeholder="Input username"/>
            </div>
            <div className="login__field">
                <label htmlFor="login-password">Password<span>*</span></label>
                <input type="text" id="login-password" placeholder="Input password"/>
            </div>
            <div className='login__bottom'>
                <button className="login__button" onClick={e => {
                    e.preventDefault();
                    // check the user 
                }}>Login</button>
                <button className='login__switch' onClick={e => {
                        e.preventDefault();
                        setMethod(method === 0? 1 : 0);
                    }
                }>Use {loginMethod[method === 0? 1 : 0]} to Login</button>
            </div>   
        </form>
    )
}
export default Login;
