import { useState } from "react";
import {nav_menu} from "./data/data";  
import "./GlobalNav.css";
import "./cssgg.css";

function GlobalNav({className}) {
    const [showMenu, setShowMenu] = useState(false);
    const li = nav_menu.map(item => {
        return (<li className="global-nav__item" key={item.name}>
            <a className="global-nav__link" href={item.path}>
                {item.name}
            </a>
        </li>)
    });
    const menuHTML = (
        <nav className={`global-nav ${className}`}>
            <ul className="global-nav__list">
                {li}
            </ul>
        </nav>
    );
    return (
        <>
            <button className="global-nav__button" onClick={() => {
                setShowMenu(!showMenu)
            }}>
                {/* {showMenu? "Close Menu" : "Open Menu"} */}
                <i className="gg-menu"/>
            </button>
            {showMenu && menuHTML}
        </>
    )
}
export default GlobalNav;
