import './App.css';
import Header from './Pages/Header';
import Main from './Pages/Main';
import Footer from './Pages/Footer';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {
  const [page, setPage] = useState("/latest_news.html");
  const [currentUser, setCurrentUser] = useState({
    username: "n/a",
    useremail: "n/a",
    userpassword: "n/a",
  });
  const [loggedin, setLoggedin] = useState(false);
  function selectPage (event) {
    event.preventDefault();
    const target = event.target.getAttribute("href");
    setPage(target);
  }
  useEffect(()=>{
    document.title = "The Animal Kingdom";
  })

  return (
    <div className='app'>
      <Header selectPage={selectPage}></Header>
      <Main page={page} selectPage={selectPage} currentUser={currentUser}
       setCurrentUser={setCurrentUser} loggedin={loggedin} setLoggedin={setLoggedin}></Main>
      <Footer></Footer>
    </div>
  );
}

export default App;
