import { useState } from "react";
import './Register.css';
// import { users } from '../data/users_data';

function Register(){
    const [username, setUsername] = useState();
    const [useremail, setUseremail] = useState();
    const [userpassword, setUserpassword] = useState();
    return (
        <form className='register__form'>
            <div className="register__field">
                <label htmlFor="register-username">Username<span>*</span></label>
                <input type="text" id="register-username" placeholder="Input username" onInput={e => {
                    setUsername(e.target.value);
                }}/>
            </div>
            <div className="register__field">
                <label htmlFor="register-email">Password<span>*</span></label>
                <input type="email" id="register-email" placeholder="Input password" onInput={e => {
                    setUseremail(e.target.value);
                }}/>
            </div>
            <div className="register__field">
                <label htmlFor="register-password">Password<span>*</span></label>
                <input type="text" id="register-password" placeholder="Input password" onInput={e => {
                    setUserpassword(e.target.value);
                }}/>
            </div>
            <div className='register__field'>
                <button className="register__button" onClick={e => {
                    e.preventDefault();
                    // check input
                    if(account === ""){
                        alert("Username or Email cannot be empty");
                        return;
                    }
                    if(password === ""){
                        alert("Password cannot be empty");
                        return;
                    }
                    if(method === 1){
                        let at = account.indexOf('@');
                        let dot = account.indexOf('.');
                        if(at === -1 || dot === -1 || (at > dot) || at === 0 || dot === account.length - 1 || at + 1 === dot){
                            alert("Email Format Invalid");
                            return;
                        }
                    }

                    // check the username and useremail
                    let users = JSON.parse(localStorage.getItem('users'));
                    if(users){
                        for(let i = 0; i < users.length; i++){
                            if(useremail === users[i].useremail || username === users[i].username){
                                alert("Username or Email Already Exist");
                                return;
                            }
                        }
                        users.push({
                            username: username,
                            useremail: useremail,
                            userpassword: userpassword,
                        });
                        localStorage.setItem('users', JSON.stringify(users));
                    }else{
                        let users = [
                            {
                                username: username,
                                useremail: useremail,
                                userpassword: userpassword,
                            }
                        ];
                        localStorage.setItem('users', JSON.stringify(users));
                    }     

                }}>Register</button>
            </div>   
        </form>
    )
}
export default Register;
